CREATE TABLE property (id INT NOT NULL GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1) CONSTRAINT property_pk PRIMARY KEY, name VARCHAR(50) DEFAULT NULL, value VARCHAR(50) DEFAULT NULL, inserted TIMESTAMP DEFAULT CURRENT_TIMESTAMP) AS SELECT null, name, value, TIMESTAMPADD("MILLISECOND",ROWNUM(),NOW()) FROM CSVREAD('src/test/resources/property-input.csv');
